  constructor(props) {
    super(props);
    this.state = {
      employees: [],
      filteredEmployees: []
    };
  }

  componentDidMount() {
    this.setState({
      employees: API.getTenRandomUser().results,
      filteredEmployees: this.state.employees
    });
  }




  render() {
    return (
