        </thead>
        <tbody>
          {this.state.filteredEmployees.map((employee, i) => { 
            console.log('hello')
            return (
            // TODO: Use a better key!
            <tr key={i}>
              <td>
                <img src={employee.picture.thumbnail} alt="Employee headshot." />
